        if (username == "mehdi" && password == "haidri") {
            window.location.pathname = "/mainpage";
        }
    
    }
    




  return (
      <div>
          <form  onSubmit={handlesubmit} >
               username  : <input type='text' name='username' onChange={()=>setusername(username) } value = {username} />
