import React, {  useRef } from 'react'
import '../App.css';

import defaultprofile from '../assets/images/defaultprofile.jpg';
import ControlPointSharpIcon from '@mui/icons-material/ControlPointSharp';
const name = window.localStorage.getItem('username');
const id = window.localStorage.getItem('id');





function Profile() {

    const input = useRef(null);


    const handlclick = () => {
        
        input.current.click();
    }

    const handlupload =  async () => {
        const file = input.current.files[0];
   
        
        if (file) {
            const formData = new FormData();
            formData.append('image', file);
            formData.append('id', id);
        fetch("http://localhost/Eservice/uploadimage.php/", {
            method: "POST",
            body : formData
            
        }).then(response => {
            if (response.ok) {
                console.log(response);
            } else {
                console.error('Failed to upload image');
            }
        }
            ).catch(error => {
                console.error('Error:', error);
            });


            
        }else {
        console.error('No file selected');
        // Handle no file selected error
    }
        
    }

  return (
      <div className='profile'>
          <div className='image'>
              <input type="file" name='file_input' ref={input} onChange={handlupload}/>
              <img className='image_profile' src={defaultprofile} onClick={handlclick}></img>
              <ControlPointSharpIcon  className='icon'/>
          </div>
          
          <h2>{name}</h2>
    </div>
  )
}

export default Profile
