import React from 'react'
import '../App.css';
import { useEffect, useState } from 'react'
import SchoolIcon from '@mui/icons-material/School';



function Login() {
    let [username, setusername] = useState("");
    let [password, setpassword] = useState("");


    let handlesubmit = async (e) => {
        e.preventDefault();
        let info = {
            username: "mehdi",
            password : "haidri"
        }

        console.log(info.username);
        console.log(info.pass);

        let data = await fetch("http://localhost/Eservice/", {
            method: 'POST',
            headers: {
                "content-type": "application/json"
            },
            body : JSON.stringify(info)
        }).then(res => 
            res
        ).then(re=>console.log(re))
        
        console.log(data);

    }
    let style = {
        width: '100px',
        height : 'auto'
    }




  return (
      <div className='login'>
          <div className='loginstyle'>
              <div className='logo'><SchoolIcon style={style}/></div>
          <form className='loginform' onSubmit={handlesubmit} >
              <div className='input1'>
               <label>  Username:</label><input type='text' name='username' onChange={(e)=>setusername(e.target.value) } value = {username} />
              </div>
              <div className='input2'>
               <label>Pasword:</label><input type='password' name='password' onChange={(e)=>setpassword(e.target.value) } value = {password}/>
              </div>
               
              
              <input type='submit' className='submit'/>
         </form>
          </div>
          
     </div>
  )
}

export default Login // if (data.username === username && data.password === password) {
            
