import React from 'react'
import { useEffect,useState } from 'react'



function Login() {
    let [username, setusername] = useState('');
    let [password, setpassword] = useState('');


    let handlesubmit = (e) => {
        e.preventDefault();

        if (username == "mehdi" && password == "haidri") {
            window.location.pathname = "/mainpage";
        }
    
    }
    




  return (
      <div className='login'>
          <form className='loginform' onSubmit={handlesubmit} >
               username  : <input type='text' name='username' onChange={(e)=>setusername(e.target.value) } value = {username} />
               pasword :  <input type='password' name='password' onChange={(e)=>setpassword(e.target.value) } value = {password}/>
              <input type='submit'/>
         </form>
     </div>
  )
}

export default Login
